// Service Worker for offline Pokemon data caching
const CACHE_NAME = 'mypokedex-v1'
const API_CACHE_NAME = 'mypokedex-api-v1'

// Resources to cache immediately
const STATIC_CACHE_URLS = [
  '/',
  '/index.html',
  '/manifest.json',
  // Add CSS and JS files that will be generated by Vite
]

// API endpoints to cache
const API_ENDPOINTS = [
  'https://pokeapi.co/api/v2/pokemon',
  'https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/',
]

// Install event - cache static resources
self.addEventListener('install', (event: ExtendableEvent) => {
  console.log('Service Worker installing.')
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('Caching static resources')
        return cache.addAll(STATIC_CACHE_URLS)
      })
      .catch((error) => {
        console.error('Failed to cache static resources:', error)
      })
  )
  // Force activation of new service worker
  self.skipWaiting()
})

// Activate event - clean up old caches
self.addEventListener('activate', (event: ExtendableEvent) => {
  console.log('Service Worker activating.')
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME && cacheName !== API_CACHE_NAME) {
            console.log('Deleting old cache:', cacheName)
            return caches.delete(cacheName)
          }
        })
      )
    }).then(() => {
      // Take control of all clients
      return self.clients.claim()
    })
  )
})

// Fetch event - serve cached content when offline
self.addEventListener('fetch', (event: FetchEvent) => {
  const { request } = event
  const url = new URL(request.url)

  // Handle API requests
  if (API_ENDPOINTS.some(endpoint => url.href.startsWith(endpoint))) {
    event.respondWith(
      caches.open(API_CACHE_NAME).then((cache) => {
        return cache.match(request).then((cachedResponse) => {
          // Return cached response if available
          if (cachedResponse) {
            return cachedResponse
          }

          // Fetch from network and cache
          return fetch(request).then((response) => {
            // Only cache successful GET requests
            if (request.method === 'GET' && response.status === 200) {
              cache.put(request, response.clone())
            }
            return response
          }).catch(() => {
            // Return offline fallback for Pokemon list
            if (url.href.includes('/pokemon?')) {
              return new Response(JSON.stringify({
                results: [
                  { name: 'pikachu', url: 'https://pokeapi.co/api/v2/pokemon/25/' },
                  { name: 'charizard', url: 'https://pokeapi.co/api/v2/pokemon/6/' },
                  { name: 'bulbasaur', url: 'https://pokeapi.co/api/v2/pokemon/1/' },
                ]
              }), {
                headers: { 'Content-Type': 'application/json' }
              })
            }
            throw new Error('Network request failed')
          })
        })
      })
    )
  } else {
    // Handle static resources
    event.respondWith(
      caches.match(request).then((response) => {
        return response || fetch(request)
      })
    )
  }
})

// Background sync for failed requests (if supported)
self.addEventListener('sync', (event: ExtendableEvent) => {
  if (event.tag === 'background-sync') {
    console.log('Background sync triggered')
    // Handle any pending operations
  }
})

// Push notifications (placeholder for future enhancement)
self.addEventListener('push', (event: PushEvent) => {
  if (event.data) {
    const data = event.data.json()
    const options = {
      body: data.body,
      icon: '/icon-192x192.png',
      badge: '/icon-192x192.png',
    }
    event.waitUntil(
      self.registration.showNotification(data.title, options)
    )
  }
})